mapper.insert:
DECLARE V_TEMP_sns_no number;
BEGIN
SELECT seq_sns_no.nextval INTO V_TEMP_sns_no FROM DUAL; 
insert all 
INTO  master_sns_tbl (sns_no, title, id, sns_date, sns_content ) 
VALUES (V_TEMP_sns_no, #{title}, #{id}, sysdate, #{sns_content, jdbcType=VARCHAR})

into master_sns_img_tbl (sns_no, title, id, sns_img)
values (V_TEMP_sns_no , #{title}, #{id}, #{item, jdbcType=VARCHAR})

SELECT 1 FROM DUAL;
END;
